TextEditor.ClientEvent:
var date=new Date();
var year;
var month=date.getMonth();

var Sys = {};
var ua = navigator.userAgent.toLowerCase();
window.ActiveXObject ? Sys.ie = ua.match(/msie ([\d.]+)/)[1] :
document.getBoxObjectFor ? Sys.firefox = ua.match(/firefox\/([\d.]+)/)[1] :
window.MessageEvent && !document.getBoxObjectFor ? Sys.chrome = ua.match(/chrome\/([\d.]+)/)[1] :
window.opera ? Sys.opera = ua.match(/opera.([\d.]+)/)[1] :
window.openDatabase ? Sys.safari = ua.match(/version\/([\d.]+)/)[1] : 0;

//以下进行测试
if(Sys.ie){
	year=(date.getYear());
}else if(Sys.chrome){
	year=(date.getYear())+1900;
}

if(month==0){
	year=year-1;
}
self.set("text",year);
view.set("context.year",year);

teYear=view.get("#teYear");
year=parseInt(teYear.get("text"));
teMonth=view.get("#teMonth");
month=teMonth.get("text");
dsPointQuotaViewHtml=view.get("#dsPointQuotaViewHtml");
hc1=view.get("#hc1");
aaWriteExcel=view.get("#aaWriteExcel");
dlgView=view.get("#dlgView");

aaWriteExcel.set("parameter",{year:year,month:month});
aaWriteExcel.execute({
	callback : function(success, obj){
		if (success) {
			dsPointQuotaViewHtml.flush();
			var html=dsPointQuotaViewHtml.getData();
			hc1.refresh(true);
			hc1.set("content",html);
			hc1.refresh(true);
        }
	}
});

var date=new Date();
var year;
var month=date.getMonth();

if(month==0){
	month=12;
}
self.set("text",month);
view.set("context.month",month);

teYear=view.get("#teYear");
year=parseInt(teYear.get("text"));
teMonth=view.get("#teMonth");
month=teMonth.get("text");
dsPointQuotaViewHtml=view.get("#dsPointQuotaViewHtml");
hc1=view.get("#hc1");
aaWriteExcel=view.get("#aaWriteExcel");
dlgView=view.get("#dlgView");

aaWriteExcel.set("parameter",{year:year,month:month});
aaWriteExcel.execute({
	callback : function(success, obj){
		if (success) {
			dsPointQuotaViewHtml.flush();
			var html=dsPointQuotaViewHtml.getData();
			hc1.refresh(true);
			hc1.set("content",html);
			hc1.refresh(true);
        }
	}
});

ToolBarButton.ClientEvent:
var teYear=view.get("#teYear");
var year=parseInt(teYear.get("text"));
var teMonth=view.get("#teMonth");
var month=parseInt(teMonth.get("text"));

var aaDownLoadFile=view.get("#aaDownLoadFile");
aaDownLoadFile.set("parameter",{year:year,month:month});
aaDownLoadFile.execute();
