DataPilot.ClientEvent:
var datas = view.get("#dsQuotaDimension.data");
var action = view.get("#uaQuotaDimension");
switch (arg.code) {
	case "+":{
		break;
	}case "-":{
		var dgQuotaDimension = view.get("#dgQuotaDimension");
		var selection = dgQuotaDimension.get("selection");
		if (selection && selection.length) {
			dorado.MessageBox.confirm("请确认是否删除",function(){
				selection.each(function(entity){
					entity.remove();
				});
				//datas.remove(selection);
				action.execute();
			});
			
			arg.processDefault = false;
		}		else {
			message = "未选中任何记录！";
			dorado.MessageBox.alert(message);
		}		
		break;
	}
}

ToolBarButton.ClientEvent:
var dgQuotaDimension=view.get("#dgQuotaDimension");
var dsQuotaDimension=view.get("#dsQuotaDimension");
var uaQuotaDimension=view.get("#uaQuotaDimension");

dorado.MessageBox.confirm("您真的要保存吗?",function(){
	uaQuotaDimension.execute({
	callback : function(success, obj){
		if (success) {
			dsQuotaDimension.flush();
			dgQuotaDimension.refresh();
        }
		else{alert("执行失败！发生异常：" + obj);}}
});

});
