DataPilot.ClientEvent:
var datas = view.get("#dsQuotaFormulaResult.data");
var action = view.get("#uaQuotaFormulaResult");
switch(arg.code){
	case "+":{
		break;
	}
	case "-":{
		var dgQuotaFormulaResult=view.get("#dgQuotaFormulaResult");
		var selection=dgQuotaFormulaResult.get("selection");
		if(selection&&selection.length){
			dorado.MessageBox.confirm("请确认是否删除",function(){	
				selection.each(function(entity){
					entity.remove();
				});
				//datas.remove(selection);
				action.execute();
			});
		
			arg.processDefault=false;
		}else{
			message = "未选中任何记录！";
			dorado.MessageBox.alert(message);
		}
		break;
	}
}

ToolBarButton.ClientEvent:
var dgQuotaFormulaResult=view.get("#dgQuotaFormulaResult");
var dsQuotaFormulaResult=view.get("#dsQuotaFormulaResult");
var uaQuotaFormulaResult=view.get("#uaQuotaFormulaResult");

dorado.MessageBox.confirm("您真的要保存吗?",function(){
	uaQuotaFormulaResult.execute({
	callback : function(success, obj){
		if (success) {
			dsQuotaFormulaResult.flush();
			dgQuotaFormulaResult.refresh();
        }
		else{alert("执行失败！发生异常：" + obj);}}
});

});
